export function resolveAbilityIcon(nameOrId){
  if (!nameOrId) return "";

  // Frontier Ballistics per-arrow icons
  // Usage: resolveAbilityIcon(`frontierBallistics:${arrowId}`)
  // Where arrowId matches ids like: "fire", "aberration_slaying".
  if (typeof nameOrId === "string" && nameOrId.startsWith("frontierBallistics:")) {
    const arrowId = nameOrId.slice("frontierBallistics:".length);
    const pascal = String(arrowId)
      .split(/[^a-zA-Z0-9]+/g)
      .filter(Boolean)
      .map(s => s.charAt(0).toUpperCase() + s.slice(1))
      .join("");
    return `/src/assets/icons/frontierBallistics/arrowOf${pascal}.png`;
  }

  const k = norm(nameOrId);
  return ABILITY_ICON_BY_KEY[k] || "";
}
